import Link from "next/link"
export const Layout = ({children}) => (
    <div>
        <div className="container">
            <nav>
                <Link href="/">
                    <a>
                        <span className="main-tilte">Hacker next</span>
                    </a>
                </Link>
            </nav>
            {children}
        </div>
        <style jsx>
            {`
                .container {
                    max-width: 800px;
                    margin: 0 auto;
                    background: #f6f6ef
                }
                nav {
                    background: green;
                    padding: 1em;
                }
                nav > * {
                    display: inline-block;
                    color: black
                }
                nav a {
                    text-decoration: none;
                }

                nav .main-title {
                    font-weight: bold;
                }
            `}
        </style>
        <style global jsx>
            {`
                .body {
                    background: white;
                    font-family: Verdana, Geneva, san-serif;
                }

            `}
        </style>
    </div>
)
